            console.log('card: '+ data.card + ' played by: ' + data.player.name)
            console.log('players');
            console.log(data.players)
            console.log(data.trick);

            const playerIndex = data.players.findIndex(p => p.id === data.player.id);
            setTrick(data.trick);
        });
    }, []);

